mapper.select:
select count(*) from medical_equipment where is_delete = 0
and name LIKE concat('%',#{name},'%')
and code = #{code}

select id, name, code, equipment_model, equipment_type, equipment_type_name, manufacturer, purchase_date, status, update_time from medical_equipment where is_delete = 0
and name LIKE concat('%',#{name},'%')
and code = #{code}
