mapper.select:
select o.id, o.code, o.position, sdd1.id as typeId, sdd1.dict_label as typeName, o.name, o.contact_name, o.contact_title, o.contact_phone, sdd2.id as districtDictId, sdd2.dict_label as district, o.county, o.address, o.remark, o.enabled, o.create_time, o.update_time from org o left join sys_dict_data sdd1 on o.type_id = sdd1.id and sdd1.status = 0 left join sys_dict_data sdd2 on o.district_dict_id = sdd2.id and sdd2.status = 0 where o.is_delete = 0
and o.name LIKE concat('%',#{keyword},'%')
and o.type_id = #{typeId}
and o.district_dict_id = #{districtDictId}
order by o.id asc

select count(*) from org o left join sys_dict_data sdd1 on o.type_id = sdd1.id and sdd1.status = 0 left join sys_dict_data sdd2 on o.district_dict_id = sdd2.id and sdd2.status = 0 where o.is_delete = 0
and o.name LIKE concat('%',#{keyword},'%')
and o.type_id = #{typeId}
and o.district_dict_id = #{districtDictId}
order by o.id asc
